div.pre:
typedef struct prgdir3 {
        long    number;
        long    length;
        long    page;
        char    comment[52];
        struct{
                short   year;
                short   month;
                short   day;
                short   hour;
                short   minute;
                short   dummy;
        } mdate;
        struct{
                short   year;
                short   month;
                short   day;
                short   hour;
                short   minute;
                short   dummy;
        } cdate;
} PRGDIR3;

